import React from 'react';
import './TodoList.css';


function TodoList (props) {

    let tasksArr = props.tasksObj;

    let todoList = tasksArr.map( (task) => {
        return(<li onHover={() => {this._closeButton.className='display'}}key={task.taskContent}>{task.taskContent}<span ref={(element => {let closeButton = element;})} onClick={ () => {
            props.removeTask(task.id)
            }
        }>X</span></li>)
    });

    // let todoList = () => {
    //     console.log(tasksArr);
    //     let liElements = [];

    //     for(let element of tasksArr) {
    //         liElements.push(<li key={element.id}>{element.taskContent} <span ref={ (element => {this._closeButton = element;})} className='close-X'>X</span> <input type='checkbox'></input></li>);
    //     }

    //     return(liElements);
    // }

    return(
        <div className='tasks-list'>
            <ul>
                {todoList}
            </ul>
        </div>
    )
}

export default TodoList;
