import React, {Component} from 'react';
import TodoList from './TodoList';

import './Todo.css';

class Todo extends Component {
    constructor (props) {
        super (props);
        this.state = {
            todoArr: [],
        }
    }



    render() {
        return(
            <div className='TodoList'>
                <input id='task' placeholder='Add task to do' />
                <button type='submit' onSubmit={this.AddTask()}>Add</button>
                <TodoList />
            </div>
        )
    }
}

export default Todo;
